import { useAppDispatch } from '../../redux/hooks/hooks';
import styles from './SearchForm.module.scss';
function SearchForm (){
    const [valueInput, setValueInput]=useState('');
    const [frends, setFrends]=useState([])
    const dispatch=useAppDispatch();
   const getFrends=async()=>{
    const response=await fetch('/frends');
    const dataFrends=await response.json();
    setFrends(dataFrends);
   }
   useEffect(()=>{
    getFrends();
   },[] )
   const handleSubmit=dispatch(searchFrends(valueInput))
return(
<form onSubmit={handleSubmit}>
    <label>Найти друзей:
        <input type='text' placeholder='Найти' value={valueInput} onChange={event=>setValueInput(event.target.value)}/>
        <button type='submit'>Найти</button>
    </label>
    </form>
   

)
}
export default SearchForm;
