import {createSlice, PayloadAction} from '@reduxjs/toolkit';
import {Frends} from '../../models/Frends';
interface MyFrendState{
    myFrend:Frends[]
}
const initialState: MyFrendState={
    myFrend:[]
}
export const MyBestFrendSlice=createSlice({
    name:'myFrend',
    initialState,
    reducers:{
        getInfoMyFrend:(state, action:PayloadAction<Frends>)=>{
            state.myFrend.push({
                id:action.payload.id,
                img:action.payload.img,
                alt:action.payload.alt,
                name:action.payload.name
            })

        }

    }
})
export const {getInfoMyFrend }=MyBestFrendSlice.actions;
export const MyBestFrenSelector=(state:MyFrendState)=>state.myFrend;
export default MyBestFrendSlice.reducer;
