import { createSlice, PayloadAction} from '@reduxjs/toolkit';
import { AppDispatch } from '../../store';
import axios from 'axios';
interface FrendsState {
    frends:[{
id:number;
firstName:string;
img:string;
alt:string;
    }],
    loading:boolean;
    hasError:string;
}
const initialState: FrendsState={
    frends:[{
        id:0,
        firstName:'',
        img:'',
        alt:''
    }],
    loading:false,
    hasError:''
}
export const FrendsSlice=createSlice({
    name:'frends',
    initialState,
    reducers:{
       getFrends:(state)=>{
        state.loading=true;
       },
       getFrendsSuccess:(state, actions:PayloadAction<{id:number, firstName:string, img:string, alt:string}>)=>{
        state.loading=false;
        state.frends.push({
            id: actions.payload.id,
            firstName: actions.payload.firstName,
            img: actions.payload.img,
            alt: actions.payload.alt,
        })

       },
       getErrorsFrends:(state, actions:PayloadAction<Error>)=>{
        state.loading=false;
        state.hasError=actions.payload.message;

       }
    }
})
export const FetchFrendsAll=()=>{
    return async (dispatch:AppDispatch)=>{
        try{
            dispatch(FrendsSlice.actions.getFrends())
            const res=await axios.get ('/frends');
            const dataFrend=res.data.result;
            dataFrend.map((item:{firsName:string, img:string, alt:string}, id:number)=>{
                const{firsName, img, alt }=item;
                return(
                    <div key={id}>
                        <p>{firsName}</p>
                        <img src={`${img}`} alt={`${alt}`}/>
                    </div>
                )

            })
            dispatch(FrendsSlice.actions.getFrendsSuccess(dataFrend))

        }
        catch(e){
            dispatch (FrendsSlice.actions.getErrorsFrends(e as Error))

        }

    }
}

export const {getFrends, getFrendsSuccess, getErrorsFrends}=FrendsSlice.actions;
export const frendsSelector=(state:FrendsState)=>state.frends;
export default FrendsSlice.reducer;
