import React, { useState } from "react";
import styles from './PageProfil.module.scss';
import myPhoto from '../../image/myPhoto.jpg';
import { useAppDispatch, useAppSelector } from '../../redux/hooks/hooks';
import { addPost } from '../../redux/features/Post/postSlice';
import {AddImagePostPage} from '../../components/AddImagePostPage/AddImagePostPage';
function PageProfil (){
    const [myPost, setMyPost]=useState('');
    const userProfil=useAppSelector(state=>state.user.user);
    const dispatch=useAppDispatch();
    const postPublick=useAppSelector(state=>state.post);
    const [imgPostAdd, setImagePost]=useState(false)
    
    
    return (
        <section className={styles.myProfil__section}>
            <div className={styles.wrapperProfil__div}>
            <div className={styles.profil__div}>
            <h3>Мой профиль</h3>
            <img src={myPhoto} alt='Мое фото'/>
            <div className={styles.myInfo__div}>
           <p>Краткая информация</p>
           <ul className={styles.profil__ul}>
            <li>Возраст: {userProfil.age}</li>
            <li>Город: {userProfil.city}</li>
            <li>ВУЗ: {userProfil.univer} </li>
           </ul>
           </div>
           </div>
           <div className={styles.post__div}>
            <h4>Добавить пост!</h4>
            <div className={styles.addText__div}>
               <textarea value={myPost} onChange={e=>setMyPost(e.target.value)} ></textarea>
               </div>
               <div className={styles.GroupButton__div}>
               <button onClick={()=>dispatch(addPost(myPost))}>Опубликовать</button>
               <button onClick={()=>setImagePost(imgPostAdd=>!imgPostAdd)}>Добавить фото</button>
               </div>
               
           </div>
           {imgPostAdd?<AddImagePostPage/>:null}
           </div>
           <div className={styles.postresult__div}>
            <h3>Мой пост</h3>
            <div className={styles.dataPost__div}>
           <p>{postPublick.post}</p>
           </div>
           </div>
        </section>

    )
}
export default PageProfil;
