import React from 'react';
import { useAppSelector } from '../../redux/hooks/hooks';
import styles from './FrendsList.module.scss';

function FrendsList (){
    const myFrends=useAppSelector(state=>state.frendsList.frends);
    const {loading, hasError}=useAppSelector(state=>state.frendsList)
    return(
        <>
         <div className={styles.table__div}>
            {myFrends.map((item, id)=>{
                const {name, img, alt}=item;
                return(
                    <div key={id} className={styles.blockFrends__div}>
                        <h4>{name}</h4>
                        <img src={`${img}`} alt={`${alt}`}/>
                    </div>
                    
                )

            })}
            </div>
        </>
    )
}
export default FrendsList;
