import React, { useState } from 'react';
import { useAppDispatch } from '../../redux/hooks/hooks';
import {searchFrends} from '../../redux/features/FrendsList/FrendsSlice';
import styles from './SearchForm.module.scss';
import { useForm, SubmitHandler } from "react-hook-form";
import {IFormInput} from '../../interface/IFormInput/IFormInput';
function SearchForm (){
    const dispatch=useAppDispatch();
    const { register, handleSubmit } = useForm<IFormInput>();
    const onSubmit: SubmitHandler<IFormInput> = data => {
        console.log(data);
        const {firstName}=data
        dispatch(searchFrends(firstName))
    }

   
return(
<form onSubmit={handleSubmit(onSubmit)} className={styles.block__form}>
    <label>Найти друзей:
        <input  placeholder='Найти друга' {...register('firstName',{required:true,maxLength:15})}/>
        <button type='submit'>Найти</button>
    </label>
    </form>
   

)
}
export default SearchForm;
