import React, { useEffect } from 'react';
import { useAppDispatch, useAppSelector } from '../../redux/hooks/hooks';
import styles from './FrendsList.module.scss';
import {FetchFrendsAll} from '../../redux/features/FrendsList/FrendsSlice';
function FrendsList (){
const dispatch=useAppDispatch();
const {loading, hasError}=useAppSelector(state=>state.frendsList)
useEffect (()=>{
    dispatch (FetchFrendsAll())
}, [dispatch])

    return(
        <>
        {loading&&<h2>Загрузка</h2>}
        {hasError&&<h3>{hasError}</h3>}
         <div className={styles.table__div}>
            {myFrends.map((item, id)=>{
                const {name, img, alt}=item;
                return(
                    <div key={id} className={styles.blockFrends__div}>
                        <h4>{name}</h4>
                        <img src={`${img}`} alt={`${alt}`}/>
                    </div>
                    
                )

            })}
            </div>
        </>
    )
}
export default FrendsList;
