import { createAsyncThunk, createSlice, PayloadAction} from '@reduxjs/toolkit';
import { AppDispatch } from '../../store';
import axios from 'axios';
interface FrendsState {
    frends:[{
id:number;
name:string;
img:string;
alt:string;
    }],
    loading:null|string;
    hasError:null;
}
const initialState: FrendsState={
    frends:[{
        id:0,
        name:'',
        img:'',
        alt:''
    }],
    loading:null,
    hasError:null
}
export const FrendsSlice=createSlice({
    name:'frends',
    initialState,
    reducers:{
        getFrends:(state, action:PayloadAction<{id:number, name:string, img:string, alt:string}>)=>{
            state.frends.push({
                id: action.payload.id,
                name: action.payload.name,
                img: action.payload.img,
                alt: action.payload.alt
            })
        }
      
    },
    extraReducers: (builder)=>{
        builder.addCase(FetchFrendsAll.pending, (state)=>{
            state.loading='Загрузка';
            state.hasError=null;

        }),
        builder.addCase(FetchFrendsAll.fulfilled, (state, action)=>{
            state.loading='Данные получены';
            state.frends=action.payload;

        }),
        builder.addCase(FetchFrendsAll.rejected, (state, action)=>{

        })

    }
})

export const FetchFrendsAll=createAsyncThunk(
    'frends/FetchFrends',
    async function () {
        const response =await fetch ('/frends');
        const data=await response.json();
        return data;
    }

) 

export const {getFrends}=FrendsSlice.actions;
export const frendsSelector=(state:FrendsState)=>state.frends;
export default FrendsSlice.reducer;
